import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import PeopleAltSharpIcon from '@material-ui/icons/PeopleAltSharp';
import Grid from '@material-ui/core/Grid';
import InsertInvitationIcon from '@material-ui/icons/InsertInvitation';
import Avatar from '@material-ui/core/Avatar';
import { lineHeight } from '@material-ui/system';

const useStyles = makeStyles({
    card: {
      width: 300,
      heigth: 500
    },
    cardAction: {
      maxWidth: 300,
      heigth: 180,
      padding: 0,
      margin: 0
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    subtitle: {
      fontSize: 8,
    },
    desc: {
      fontSize: 14,
    },
    developer: {
      color: '#ffffff',
      backgroundColor: '#ff1744',
      fontSize: 12,
      marginRight: 4,
      marginLeft: 4
    },
    designer: {
      color: '#ffffff',
      backgroundColor: '#ffc107',
      fontSize: 12,
      marginRight: 4,
      marginLeft: 4
    },
    planner: {
      color: '#ffffff',
      backgroundColor: '#4caf50',
      fontSize: 12,
      marginRight: 4,
      marginLeft: 4
    },
    cardContent: {
      padding: 5
    }
  });



export default function PeopleBox(props) {
    const classes = useStyles();
    const {state} = props
    return (
    <Card className={classes.card}>
        <CardActions className={classes.cardAction}>
            <img src={state.img} alt="11" style={{width: "100%", height: "180px", objectFit: 'cover', opacity: 0.5}}/>
                <Typography variant="h5" component="h2">
                    {state.name}
                </Typography>
        </CardActions>
        <CardContent className={classes.cardContent}>
            <Typography variant="h5" component="h2">
            {state.title}
            </Typography>
            <Grid container spacing={12}>
                <Grid container item xs={6}>
                    <Typography className={classes.subtitle} color="textSecondary" gutterBottom>
                    {state.people}
                    </Typography>
                    <Grid container>
                        <PeopleAltSharpIcon />
                        <Typography variant="body2">
                            {state.remain}명 남았습니다.
                        </Typography>
                    </Grid>
                    <Grid container>
                        <InsertInvitationIcon />
                        <Typography variant="body2">
                            {state.day}일 남았습니다.
                        </Typography>
                    </Grid>
                </Grid>
                <Grid container item xs={6}>
                    <div style={{textAlign: "center", lineHeight: '20px'}}>
                        <Avatar variant="circle" className={classes.developer}>
                            {state.developer}명
                        </Avatar>
                        <Typography variant="caption" align="justify">
                            개발자
                        </Typography> 
                    </div>
                    <div style={{textAlign: "center", lineHeight: '20px'}}>
                        <Avatar variant="circle" className={classes.designer}>
                            { state.designer }명
                        </Avatar>
                        <Typography variant="caption" align="justify">
                            디자이너
                        </Typography> 
                    </div>
                    <div style={{textAlign: "center", lineHeight: '20px'}}>
                        <Avatar variant="circle" className={classes.planner}>
                            {state.planner}명
                        </Avatar>
                        <Typography variant="caption" align="justify">
                            기획자
                        </Typography> 
                    </div>
                </Grid>
            </Grid>
        </CardContent>
    </Card>
    );
  }
